import React, { useState } from "react";
import img1 from "../../../images/landing/c_phone.png";
import img2 from "../../../images/landing/c_mail.png";
import { message } from "antd";
import { ContactSection } from "./styles";

export const Contact = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [organization, setOrganization] = useState("");
  const [description, setDescription] = useState("");

  const success = () => {
    message.success({
      content:
        "Hello there! Thank you for reaching out. We will get back to you as quick as humanly possible.",
      className: "messageCont",
      icon: <SmileOutlined />,
      style: {
        display: "flex",
        justifyContent: `center`,
        alignItems: `center`,
      },
    });
  };

  const warning = () => {
    message.warning({ content: "All fields need to be filled" });
  };
  const [errors, setErrors] = useState(false);
  const validation = () => {
    let errors = {};
    if (name.length < 3) {
      errors.color = "red";
    } else {
      errors.color = "";
    }
    if (email.length < 3) {
      errors.color = "red";
    } else {
      errors.color = "";
    }
    return errors;
  };
  function signUp() {
    setErrors(validation());
    warning();
  }
  const onFinish = async (values) => {
    const data = new FormData();
    data.append("name", name);
    data.append("email", email);
    if (phone === undefined) {
      data.append("phone", "-");
    } else {
      data.append("phone", phone);
    }
    if (organization === undefined) {
      data.append("organization", "-");
    } else {
      data.append("organization", organization);
    }
    if (description === undefined) {
      data.append("Description", "-");
    } else {
      data.append("Description", description);
    }

    var url =
      "https://script.google.com/macros/s/AKfycby6DXXQypCUffF3AwK4ClaGflKHhJlgCFMPbt5qbs5wLk75IpVvwV-BEp1ntgd8zVQa/exec";

    await fetch(url, {
      method: "POST",
      body: data,
      mode: "no-cors",
    })
      .then(function (response) {
        success();
        setName("");
        setPhone("");
        setEmail("");
        setOrganization("");
        setDescription("");
        setErrors(true);
      })
      .catch(function (err) {
        setErrors(true);
        message.error({
          content: err.message,
          className: "messageCont",
          icon: <SmileOutlined rotate={180} />,
        });
      });
  };

  return <ContactSection></ContactSection>;
};
